'use strict';

module.exports = function(grunt) {

  grunt.registerTask('custom_plugin', 'Practice grunt plugin', function() {

    // Merge user options with these defaults
    var options = this.options({
      before: 'Hello, ',
      content: 'anonymous',
      after: '!!!'
    });

    // Print out a custom message
    grunt.log.writeln(options.before + options.content + options.after);

  });

};